export class StorageWrap{constructor(t){this._storage=t||{getItem:()=>null,setItem(){throw new Error("")},removeItem(){}}}get(t){return this._storage.getItem(t)}getAsJSON(t){try{return JSON.parse(this.get(t)||"")}catch(t){return null}}set(t,e){try{return this._storage.setItem(t,e),!0}catch(t){return!1}}setAsJSON(t,e){return this.set(t,JSON.stringify(e))}remove(t){this._storage.removeItem(t)}}